/**
 * @module routes/controllers/user
 * @fileoverview User route's controller. Handle all business logic relative to
 *    a particular user or a set of users.
 * @exports {Object} Functions to attach to the `users` router.
 */


/**
 * @function login
 * @description Log a user in based on the received form data from the client.
 * @param {Object} req The request object generated by `Express`.
 * @param {Object} res The response object generated by `Express`.
 * @param {Function} next The function to call when this method is done executing
 *    and does not return or render anything (no `res` methods called).
 */
const createAccount = (req, res, next) => {
  // Capture user-submitted form from client.
  //console.log(req);
  console.log(req.body);
  const fs = require("fs");
  fs.readFile('.hiddenCreds', (err, data) => {
      if (err) {
        throw err;
      } else {
        json = JSON.parse(data.toString());
        console.log(json);
        var email = req.body["email"];

        console.log("API email: " + email);

        var mysql = require("mysql");

        var con = mysql.createConnection({
          host: json[0]["host"],
          user: json[0]["user"],
          password: json[0]["password"],
          database: json[0]["database"]
        });
        con.connect(function(err) {
          if (err) throw err;
          console.log("Connected!");
        });
        statement = ("INSERT INTO account(FIRSTNAME, LASTNAME, ADDRESS," +
                     " CITY, ZIP, EMAIL, PHONENUMBER, USERNAME, PASSWORD, " +
                     "CREATIONDATE, UPDATEDDATE, ISACTIVE, ISADMIN)" +
                     "VALUES('" + req.body["first_name"] + "' ," +
                            "'" + req.body["last_name"] + "' ," +
                            "'" + req.body["address"] + "' ," +
                            "'" + req.body["city"] + "' ," +
                            "'" + req.body["zip"] + "' ," +
                            "'" + req.body["email"] + "' ," +
                            "'" + req.body["phone_number"] + "' ," +
                            "'" + req.body["username"] + "' ," +
                            "'" + req.body["password"] + "' ," +
                            "CURRENT_TIMESTAMP, " +
                            "CURRENT_TIMESTAMP, " +
                            "True, " +
                            "False)");

        var response = "";
        con.query(statement, function(err, result) {
          if (err) {
            console.log("bad account creation");
            res.setHeader('Content-Type', 'plain/text');
            res.send("account creation failed!");
            throw err;
          } else {
            console.log(result);

            var nodemailer = require('nodemailer');

            var transporter = nodemailer.createTransport({
              service: 'gmail',
                auth: {
                  user: json[1]["email"],
                  pass: json[1]["password"]
                }
            });
            console.log("EMAIL JSON: " + json[1]["email"]);
            console.log("PASSWORD JSON: " + json[1]["password"]);
            console.log("CA EMAIL: " + req.body["email"]);
            var mailOptions = {
              from: 'compscigoat@gmail.com',
              to:   'aantaki316@gmail.com',
              subject: 'Welcoe to Sprout Creek Farm',
              text: 'Your account for Sprout Creek Farm has been created'
            };

            transporter.sendMail(mailOptions, function(error, info){
              if (error) {
                console.log(error);
              } else {
                console.log('Email sent: ' + info.response);
              }
            });
              console.log("Sending email and account created to... " + req.body["email"]);
              res.setHeader('Content-Type', 'plain/text');
              res.send("account created!");
          }
        });
      }
  });
};

module.exports = {
  createAccount,
};
