/**
 * @module routes/controllers/forgotPassword
 * @fileoverview forgot Password route's controller. Exports functions to be used by each
 *    route handler.
 * @exports {Object} Functions to attach to the `login` router.
 */


/**
 * @function sendCreateAccountPage
 * @description Send the base page rendered by `Handlebars.js`. Compilation
 *    of HBS templates is handled by the HBS engine.
 * @param {Object} req The request object generated by `Express`.
 * @param {Object} res The response object generated by `Express`.
 * @param {Function} next The function to call when this method is done executing
 *    and does not return or render anything (no `res` methods called).
 */
const sendCreateAccountPage = (req, res, next) => {
  res.render('create_account', { title: 'Sprout Creek Farm Create Account', page: 'login' });
};


module.exports = {
  sendCreateAccountPage,
};
