/**
 * @module routes/controllers/forgotPassword
 * @fileoverview forgot Password route's controller. Exports functions to be used by each
 *    route handler.
 * @exports {Object} Functions to attach to the `login` router.
 */
 const cookies = require('../../scripts/cookie-helper.js');


/**
 * @function sendFrogtPasswordPage
 * @description Send the base page rendered by `Handlebars.js`. Compilation
 *    of HBS templates is handled by the HBS engine.
 * @param {Object} req The request object generated by `Express`.
 * @param {Object} res The response object generated by `Express`.
 * @param {Function} next The function to call when this method is done executing
 *    and does not return or render anything (no `res` methods called).
 */
const sendForgotPasswordPage = (req, res, next) => {
  cookies.handleNormalPageCookie(req.cookies)
    .then(res_cookie => {
      if (res_cookie == null || res_cookie == "undefined") {
        res.clearCookie("CID");
      } else {
        res.cookie("CID", res_cookie);
      }
      page = "";
      if (req.cookies["CID"] == null || req.cookies["CID"] == "undefined") {
        page = "forgot_password";
      // check if the cookie is valid
      } else {
        if (req.cookies["CID"]["isAdmin"] == 1) {
          page = "admin_dashboard";
        } else {
          page = "user_dashboard";
        }
      }
      if (page == "forgot_password") {
        res.render('forgot_password', { title: 'Sprout Creek Farm Forgot Password',
                                        page: 'login' });
      } else {
        res.redirect(page);

      }
    });
};


module.exports = {
  sendForgotPasswordPage,
};
