/**
 * @module routes/controllers/login
 * @fileoverview Login route's controller. Exports functions to be used by each
 *    route handler.
 * @exports {Object} Functions to attach to the `login` router.
 */

const cookies = require('../../scripts/cookie-helper.js');

/**
 * @function sendLoginPage
 * @description Send the base page rendered by `Handlebars.js`. Compilation
 *    of HBS templates is handled by the HBS engine.
 * @param {Object} req The request object generated by `Express`.
 * @param {Object} res The response object generated by `Express`.
 * @param {Function} next The function to call when this method is done executing
 *    and does not return or render anything (no `res` methods called).
 */
const sendMarketItemPage = (req, res, next) => {
  // will get the item id
  // will be used to call api and use the system
  var itemNum = req.baseUrl.split("/")[2];
  console.log(itemNum);
  cookies.handleNormalPageCookie(req.cookies)
    .then(res_cookie => {
      var request = require("request");
      if (res_cookie == "undefined" || res_cookie == null) {
        res.clearCookie("CID");
      } else {
        res.cookie("CID", res_cookie);
      }
      var options ={
        method: 'GET',
        url: 'http://localhost:3000/api/getItem/' + itemNum,
      };

      console.log(options);

      request(options, function (error, response, body) {
        if (error) {
          console.log(error.message);
          res.redirect('../market');
        } else {
          //itemsList = JSON.parse(body.toString())
          var data = JSON.parse(body.toString());
          //console.log(data);
          res.render('marketItem', {
            title: 'Sprout Creek Farm Market | ' + data[0]["itemname"],
            page: 'market',
            item: data});
        }
      });
    });
};


module.exports = {
  sendMarketItemPage,
};
